class ZY00QuerySet(models.QuerySet):
    def actifs(self):
        """Retourne les employs avec AU MOINS UN contrat actif"""
        aujourdhui = timezone.now().date()
        # Note: ZYCO sera dfini plus tard, mais c'est OK dans une mthode
        return self.filter(
            contrats__actif=True,
        ).filter(
            Q(contrats__date_fin__isnull=True) |
            Q(contrats__date_fin__gte=aujourdhui)

