def format_heures(value):
    """
    Convertit un nombre dcimal d'heures en format 'Xh Ymin'.
    Exemple: 6.25 -> '6h 15min', 2.0 -> '2h', 0.5 -> '0h 30min'
    """
    if value is None:
        return '0h'
    try:
        total_minutes = int(float(value) * 60)
