def format_minutes(value):
    """
    Convertit un nombre de minutes en format 'Xh Ymin'.
    Exemple: 375 -> '6h 15min', 120 -> '2h', 30 -> '0h 30min'
    """
    if value is None or value == 0:
        return '0h'
    try:
        total_minutes = int(value)
        heures = total_minutes // 60

