    """
    Dtecte les changements de statut pour enregistrer l'ancien statut
    """
    if instance.pk:
        try:
            old_instance = ZDDA.objects.get(pk=instance.pk)
            instance._old_statut = old_instance.statut
        except ZDDA.DoesNotExist:
            instance._old_statut = None

