        return None
    aujourdhui = timezone.now().date()
    jours_restants = (tache.date_fin_prevue - aujourdhui).days
    if 0 <= jours_restants <= 2:  # Dans les 2 prochains jours
        message = f" chance proche ({jours_restants} jour(s)) : {tache.titre}"
